import React, {useState} from 'react'
import "./button.css"

const Button =()=>{


buttonAction.addEventListener("onClick", alert)




  const alertfNlN = () =>{

    const fN = document.querySelector(".fN");
    const lN = document.querySelector(".lN");
    const buttonAction = document.querySelector("")

    
    if(this.fN.current.value || this.lN.current.value != null || undefined || ''){
  
      alert(`Is your name ${this.fN.current.value} ${this.lN.current.value} by any chance?`)
    } 
  }

  const [counter, setCounter] = useState(0);
  
  return (<button className="btn" onClick={alertfNlN()} type ="button" onMouseOver= {() => setCounter(counter + 1)} >Register now! {counter} hover(s)</button>
  )
};
export default Button;
