import React, {useEffect} from 'react'
import "./input.css"


const Input =()=> {
  const fN = document.querySelector(".fn");
  const lN = document.querySelector(".ln").value;
  React.useEffect(() =>{

    
    
        window.addEventListener("keydown", (e)=>{
    
      console.log(Object.getOwnPropertyNames(fN)[4])


      if (e.code === "Enter"){
       console.log(fN.value)
        if(fN.value || lN.value != null || undefined || ''){
          
          
          alert(`Is your name ${fN.value} ${lN.value} by any chance?`)
        }
      }
    });
  }, [])

  return (<>
  <label>First Name
    <input className='fn'/>
  </label>

  <label>Last Name
    <input className='ln'/>
  </label>

  <label type = "email">Email
    <input className='email'/>
  </label>

  <label>Phone
    <input className='phone'/>
  </label>

  <label type= "password">Password
    <input className='password'/>
  </label>
  
  <label type= "password">Confirm Password
    <input className='confpass'/>
  </label>

  </>)
}
export default Input;
