import React, {useState, useEffect} from 'react'
import "./button.css"

//press button to get alert
const Button = ()=>{

  React.useEffect(() =>{
    
    class CustomTextInput extends React.Component {
      constructor(props) {
        super(props);
        // create a ref to store the textInput DOM element
        this.fN = React.createRef();
        this.lN = this.lN.bind(this);
      }
     }

     
const focusfN=()=> {
  // Explicitly focus the text input using the raw DOM API
  // Note: we're accessing "current" to get the DOM node
  this.fN.current.focus();
}

const buttonAction = document.querySelector(".btn");
const fN = document.querySelector(".fn");
const lN = document.querySelector(".ln");
buttonAction.addEventListener("onClick", ()=>{
  console.log(buttonAction)
  if(fN.value || lN.value != null || undefined || ''){
    
      alert(`Is your name ${fN.value} ${lN.value} by any chance?`)
    }
});
}, [])



  const [counter, setCounter] = useState(0);
  
  return (<button className="btn" ref type ="button" onMouseOver= {() => setCounter(counter + 1)} >Register now! {counter} hover(s) </button>
  )
};
export default Button;
